function varargout = processorGUI(varargin)
    % GUI Initiation Function - DO NOT EDIT
    gui_Singleton = 1;
    gui_State = struct('gui_Name',       mfilename, ...
     'gui_Singleton',  gui_Singleton, ...
     'gui_OpeningFcn', @processorGUI_OpeningFcn, ...
     'gui_OutputFcn',  @processorGUI_OutputFcn, ...
     'gui_LayoutFcn',  @processorGUI_LayoutFcn, ...
     'gui_Callback',   []);
    if nargin && ischar(varargin{1})
        gui_State.gui_Callback = str2func(varargin{1});
    end

    if nargout
        [varargout{1:nargout}] = gui_mainfcn(gui_State, varargin{:});
    else
        gui_mainfcn(gui_State, varargin{:});
    end
end

function processorGUI_OpeningFcn(hObject, eventdata, handles, varargin) %#ok<*INUSL>
    %Software initialization function - first function run
    % This function has no output args, see OutputFcn.
    % hObject    handle to figure (see GUIDATA)
    % varargin   command line arguments to processorGUI (see VARARGIN)
    % Choose default command line output for processorGUI
    handles.output = hObject;
    % Update handles structure
    guidata(hObject, handles);
    global processorObj;
    processorObj = processor();
end


function varargout = processorGUI_OutputFcn(hObject, eventdata, handles) 
    % --- Outputs from this function are returned to the command line.
    % varargout  cell array for returning output args (see VARARGOUT);
    % hObject    handle to figure
    % eventdata  reserved - to be defined in a future version of MATLAB
    % handles    structure with handles and user data (see GUIDATA)
    % Get default command line output from handles structure
    varargout{1} = handles.output;
end

