% --- Executes on button press in save.
function save_Callback(hObject, eventdata, handles)
    global processorObj;
    set(handles.graphAxes, 'Units', 'pixels');
    graphCoordinates = get(handles.graphAxes, 'Position');
    margin = [-25, -25, 50, 50];
    f = getframe(handles.figure1, graphCoordinates + margin);
    imwrite(f.cdata, [processorObj.getSaveLocation(), processorObj.getTitle(), '-graph-X', ...
        int2str(processorObj.getX()), 'Y', int2str(processorObj.getY()), 'XR', ...
        int2str(processorObj.getXRadius()), 'YR', int2str(processorObj.getYRadius()), '.tif']);
    set(handles.graphAxes, 'Units', 'characters');
    msgbox('Graph Saved')
end

% --- Executes on button press in percentGraph.
function imageDisplay_Callback(hObject, eventdata, handles)
    global processorObj;
    img = imread(defaults.defaultLocation(processorObj.getSaveLocation(), processorObj.getTitle(), 'reg', int2str(processorObj.getBand()), int2str(1)));
    axes(handles.graphAxes), cla;
    imagesc(img), colormap (gray)
    axis image;
end

